#!/usr/bin/env nix-shell
#![allow()] /*
#!nix-shell -i bash -p rustc
rsfile="$(readlink  -f  $0)"
binfile="/tmp/$(basename  "$rsfile").bin"
rustc  "$rsfile"  -o  "$binfile"  --edition=2021  &&  exec  "$binfile"  $@  ||  exit  $?
*/
fn  main()  {
  for  argument  in  std::env::args().skip(1)  {
  println!("{}",  argument);
  };
  println!("{}",  std::env::var("HOME").expect(""));
} 
```

