 name: string;
 id: safeint;
 status: "Alive" | "Dead";
 class: Class;
}
 enum Class { warrior; wizard; }
 @minValue(1)
@maxValue(20)
scalar statValue extends uint8;
 model Stats {
 strength: statValue;
 intelligence: statValue;
 dexterity: statValue;
}
 op getCharacter(@path id: safeint): Character;
