type Post {
    id: ID!
    body: String
    date: Date
    Author: User
}

type User {
    id: ID!
    username: String
    first_name: String
    last_name: String
    full_name: String
    name: String @deprecated
    avatar_url: Url
}

scalar Url
scalar Date

type Query {
    Post(id: ID!): Post
    Posts(limit: Int, skip: Int, sort_field: String, sort_order: String): [Post]
    User(id: ID!): User
}

type Mutation {
    createPost (
        body: String
    ): Post
    deletePost(id: ID!): Tweet
}
